package week1.day5;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 51; i < 72; i++) {
			System.out.println(i);
		}
		System.out.println();
		for (int j = 71; j > 50; j--) {
			System.out.println(j);
		}
	}

}
