package week1.day5;

public class OddNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * for (int i = 1; i < 21; i=i+2) {
		 * 
		 * System.out.println(i);
		 * 
		 */

		for (int j = 1; j < 21; j++) {
			if (j % 2 != 0) {						//if(j%2>0) also similar condition
				System.out.println(j);
			}
		}
	}

}
