package week1.day5;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = -5;
		if (num > 0) {
			System.out.println(num + " is a positive number");
		}

		else {
			System.out.println(num + " is a negative number");
		}
	}

}
